function initializeKeyboardNavigation(){
detectKeyboardUser();
document.addEventListener('keydown', (event)=>{
if (event.ctrlKey || event.metaKey){
switch (event.key){
case '1':
event.preventDefault();
switchTab('itinerary');
break;
case '2':
event.preventDefault();
switchTab('map');
break;
case '3':
event.preventDefault();
switchTab('budget');
break;
case '4':
event.preventDefault();
switchTab('documents');
break;
case 'f':
event.preventDefault();
const searchInput=document.querySelector('.search-input');
if (searchInput){
searchInput.focus();
announceToScreenReader('Search field focused');
}
break;
}
}
if (event.target.closest('.tab-navigation')){
const tabs=document.querySelectorAll('.tab-btn');
const currentTab=document.querySelector('.tab-btn[aria-selected="true"]');
const currentIndex=Array.from(tabs).indexOf(currentTab);
switch (event.key){
case 'ArrowLeft':
case 'ArrowUp':
event.preventDefault();
const prevIndex=currentIndex > 0 ? currentIndex - 1 : tabs.length - 1;
tabs[prevIndex].focus();
tabs[prevIndex].click();
break;
case 'ArrowRight':
case 'ArrowDown':
event.preventDefault();
const nextIndex=currentIndex < tabs.length - 1 ? currentIndex+1 : 0;
tabs[nextIndex].focus();
tabs[nextIndex].click();
break;
case 'Home':
event.preventDefault();
tabs[0].focus();
tabs[0].click();
break;
case 'End':
event.preventDefault();
tabs[tabs.length - 1].focus();
tabs[tabs.length - 1].click();
break;
}
}
if (AppState.currentTab==='itinerary'){
const activities=document.querySelectorAll('.activity-item');
const currentIndex=AppState.selectedActivity || 0;
switch (event.key){
case 'ArrowDown':
event.preventDefault();
if (currentIndex < activities.length - 1){
highlightActivity(currentIndex+1);
}
break;
case 'ArrowUp':
event.preventDefault();
if (currentIndex > 0){
highlightActivity(currentIndex - 1);
}
break;
case 'Enter':
if (activities[currentIndex]){
activities[currentIndex].click();
}
break;
case 'Escape':
const searchInput=document.querySelector('.search-input');
if (searchInput){
searchInput.value='';
showAllActivities();
announceToScreenReader('Search cleared, showing all activities');
}
break;
}
}
});
}
